import BackTop from '~/components/BackTop';
import axios from '~/plugins/axios';
import markdown from '~/plugins/md';
import Gitment from 'gitment';
import '~/assets/css/gitment.css';
const CommentTheme = {
  render(state, instance) {
    const container = document.createElement('div');
    container.lang = 'en-US';
    container.className = 'gitment-container gitment-root-container';
    container.appendChild(instance.renderHeader(state, instance));
    container.appendChild(instance.renderEditor(state, instance));
    container.appendChild(instance.renderComments(state, instance)); // container.appendChild(instance.renderFooter(state, instance))

    return container;
  }

};
export default {
  components: {
    BackTop
  },

  async asyncData({
    route,
    error
  }) {
    let {
      data: {
        data
      }
    } = await axios.get('/blog/post/' + route.params.id + '?guide=1');

    if (!data._id) {
      error({
        statusCode: 404
      });
    }

    return {
      blog: data
    };
  },

  computed: {
    content() {
      return markdown.render(this.blog.content);
    },

    prev() {
      if (this.blog.link.prev._id) {
        return this.blog.link.prev;
      }

      return false;
    },

    next() {
      if (this.blog.link.next._id) {
        return this.blog.link.next;
      }

      return false;
    }

  },

  head() {
    return {
      title: this.blog.title,
      meta: [{
        hid: 'keywords',
        name: 'keywords',
        content: 'cyy,博客,前端,' + this.blog.tags.join(',')
      }, {
        hid: 'description',
        name: 'description',
        content: this.blog.description
      }]
    };
  },

  mounted() {
    new Gitment({
      id: this.blog._id,
      // 可选。默认为 location.href
      owner: 'cyyjs',
      repo: 'blog_comment',
      oauth: {
        client_id: 'ac10f3c942ffb50e2d9b',
        client_secret: 'be6b45d2000cb68ce4ecc23f3d34eb4737091e4f'
      },
      theme: CommentTheme
    }).render('comment');
  }

};

